INSERT INTO `lotels` (`id`, `lotel_name`, `introduct`, `price_day`, `thumb`, `image_list`, `contact`, `person`, `created_at`, `updated_at`, `address`, `commune`, `district`, `province`, `located`, `equipment`, `id_category`) VALUES
(56, 'A25 Hotel - Đội Cấn 1', '<p>Tọa lạc tại một vị trí trung tâm ở thành phố Hà Nội, khách sạn đội cấn nằm cách Hồ Tây tuyệt đẹp chỉ 1 km. Khách có thể truy cập Wi-Fi miễn phí trong toàn bộ khuôn viên. Trong số các dịch vụ được cung cấp (với một khoản phí) tại đây có dịch vụ đưa/đón sân bay, giặt thường và giặt hấp. Khách có thể gửi hành lý miễn phí tại quầy lễ tân 24 giờ.</p>', 200000, '1700460830-thumb.jpg', 'd12f3b5c457a92abbc87939406df183f-details.jpg|c5a4e7e6882845ea7bb4d9462868219b-details.jpg|ba3c736667394d5082f86f28aef38107-details.jpg|bd430257087f92e5322919c84dc99f32-details.jpg', '0354567667', 10, '2023-11-20', '2023-12-09 07:07:09', '25 ngõ 379/8 Đội Cấn', 195, 135, 18, 'https://maps.app.goo.gl/1MEMzdMmosAkg9eCA', 'Bed , Car', 1),
(57, 'A25 Hotel - 109 Trúc Bạch', 'Chi nhánh thuộc hệ thống khách sạn A25 với trang thiết bị đạt tiêu chuẩn 3 sao. Cách hồ Trúc Bạch 100m, cách Con Đường Gốm Sứ khoảng 10 phút đi bộ, tọa lạc ở trung tâm Quận Ba Đình - Hà Nội, gần Lăng chủ tịch Hồ Chí Minh,', 400000, '1700553829-thumb.jpg', '299dc35e747eb77177d9cea10a802da2-details.jpg|798ed7d4ee7138d49b8828958048130a-details.jpg|2c3279b0b059b2665593834a264d7de6-details.jpg|80c94c09453dfe07681fde78e769353f-details.jpg', '0354567669', 10, '2023-11-21', '2023-11-28 19:19:36', '109 Trúc Bạch', 195, 135, 18, 'https://maps.app.goo.gl/moi8UTcheC8YrwWP9', 'Wifi , Parking , Bed , bath , Car', 1),
(58, 'Hana Stay', 'Hana Stay Apartment toạ lạc tại khu vực / thành phố Cống Vị.  nơi nghỉ nằm cách Hanoi Railway Station 3,68 km.  Có rất nhiều điểm tham quan lân cận như Thu Le Zoo Hanoi ở khoảng cách 0,69 km, và LOTTE Observation Deck ở khoảng cách 0,66 km.', 300000, '1700554137-thumb.png', 'c035226640b6b89ffaf333f54e523c10-details.png|98b418276d571e623651fc1d471c7811-details.png|161882dd2d19c716819081aee2c08b98-details.png|d7488039246a405baf6a7cbc3613a56f-details.png|1299c1b7a9e0c2bf41af69c449464a49-details.png|d27b95cac4c27feb850aaa4070cc4675-details.png|2354c276f1c9156f4b97a11a7aa41254-details.png|0530e22dea41e24a039563139cdc215e-details.png', '0354567669', 12, '2023-11-21', '2023-11-21 08:08:57', 'Số 148 Phan Kế Bính', 296, 135, 18, 'https://maps.app.goo.gl/tojBjbWyps5Sqxw29', 'Wifi , Parking , Bed', 1),
(59, 'A25 Hotel - Dịch Vọng Hậu', 'Khách sạn A25 Ngõ 25 Dịch Vọng Hậu có vị trí thuận lợi nằm gần trường Đại học Sư phạm, Đại học thương mại, Học viện Báo chí tuyên truyền, trung tâm thương mại Indochina Plaza,… và chỉ mất vài phút để di chuyển tới Lanmark Tower.', 150000, '1700556935-thumb.jpg', 'd77c703536718b95308130ff2e5cf9ee-details.jpg|a267f936e54d7c10a2bb70dbe6ad7a89-details.jpg|22b1f2e0983160db6f7bb9f62f4dbb39-details.jpg', '0354567669', 12, '2023-11-21', '2023-11-21 08:55:35', 'Ngõ 25', 474, 143, 18, 'https://maps.app.goo.gl/YWej7vvmGM1YUC7R8', 'Wifi , Parking , Bed', 1),
(60, 'A25 Hotel - 26 Hàng Nón', 'A25 Hotel 26 Hàng Nón nằm tại trung tâm khu Phố Cổ - Quận Hoàn Kiếm - Hà Nội, là chi nhánh thuộc hệ thống khách sạn A25 với trang thiết bị đạt tiêu chuẩn 3 sao, trong khu vực 36 phố phường, cách hồ Hoàn Kiếm khoảng 500m, cách chợ Đồng Xuân khoảng 800m. Xung quanh khách sạn là khu mua sắm, khu ăn uống mang đặc trưng của Hà Nội là một trong những địa điểm nên đến nhất khi khách du lịch tới Thủ Đô.  A25 Hotel 26 Hàng Nón được thiết kế theo phong cách Hà Nội xưa với 22 phòng nghỉ hiện đại, phảng phất phong cách Pháp. Tới 26 Hàng Nón quý khách sẽ dễ dàng cảm nhận không khí đông đúc nhưng vẫn thanh lịch của Thủ Đô. Đặc biệt vào những ngày cuối tuần du khách có thể hòa mình vào phố cổ đi bộ với những nét đặc trưng khó quên.', 150000, '1700557153-thumb.jpg', '69dafe8b58066478aea48f3d0f384820-details.jpg|0ef41260a5b096182ee7e45b942cd46d-details.jpg|654ad60ebd1ae29cedc37da04b6b0672-details.jpg|db2b4182156b2f1f817860ac9f409ad7-details.jpg', '0354567669', 5, '2023-11-21', '2023-11-21 08:59:13', 'Ngõ 25 Hàng Nón', 260, 132, 18, 'https://maps.app.goo.gl/U6XW3a1gvdGr7eDw6', 'Wifi , Parking , Bed', 1),
(61, 'A25 Hotel - 19 Phan Đình Phùng', 'hi nhánh thuộc hệ thống khách sạn A25 với trang thiết bị đạt tiêu chuẩn 3 sao. Khách sạn A25 19 Phan Đình  là khách sạn sang trọng nằm trên một trong những con đường đẹp nhất Hà Nội với vỉa hè rộng và hàng sấu thơ mộng - một trong những nét đặc sắc của Thủ đô.', 200000, '1700557378-thumb.jpg', '3a0844cee4fcf57de0c71e9ad3035478-details.jpg|7d6548bdc0082aacc950ed35e91fcccb-details.jpg|e48e13207341b6bffb7fb1622282247b-details.jpg|ea50fb37fdda7dd9b3b42c6f4a8b3d3f-details.jpg', '0354567669', 8, '2023-11-21', '2023-11-21 09:02:58', '19 Phan Đình Phùng', 306, 135, 18, 'https://maps.app.goo.gl/U6XW3a1gvdGr7eDw6', 'Wifi , Parking , Bed', 1),
(62, 'A25 Hotel - 45 Phan Chu Trinh', 'A25 Hotel 45 Phan Chu Trinh là chi nhánh thuộc hệ thống khách sạn A25 với trang thiết bị đạt tiêu chuẩn 3 sao, nằm gần nhiều địa danh nổi tiếng như nhà hát lớn, Quảng trường Cách Mạng Tháng 8, Hồ Hoàn Kiếm...', 200000, '1700558103-thumb.jpg', 'b4681a619cf018eed690452faeb0e94f-details.jpg|d693d554e0ede0d75f7d2873b015f228-details.jpg|c0356641f421b381e475776b602a5da8-details.jpg|95192c98732387165bf8e396c0f2dad2-details.jpg|373e4c5d8edfa8b74fd4b6791d0cf6dc-details.jpg', '0354567669', 9, '2023-11-21', '2023-11-21 09:15:03', '45', 485, 132, 18, 'https://maps.app.goo.gl/wxWTdDdyjXR7Crtu8', 'Wifi , Parking , Bed', 1),
(63, 'A25 Hotel - 385 Hoàng Quốc Việt', 'A25 Hotel tại ngõ 385 Hoàng Quốc Việt, Quận Cầu Giấy, Hà Nội là khách sạn đầu tiên trong hệ thống khách sạn A25. Bên cạnh ý nghĩa về kinh doanh, khách sạn A25 Hoàng Quốc Việt còn mang đậm nét đẹp truyền thống của Tập đoàn.  KHÁCH SẠN CÓ VỊ TRÍ THUẬN LỢI Nằm gần tuyến đường từ nội thành Hà Nội đi các khu công nghiệp Canon, Sam Sung... A25 Hotel Hoàng Quốc Việt là mộttrong những lựa chọn ưu tiên của các công ty lớn khi cử cán bộ công tác ở thủ đô Hà Nội. PHÒNG NGHỈ SANG TRỌNG, HIỆN ĐẠI VÀ ĐẦY ĐỦ TIỆN NGHI Mọi phòng nghỉ ở khách sạn A25 Hoàng Quốc Việt đều được trang bị những vật dụng thiết yếu và tiện nghi nhằm phục vụ tốt nhất nhu cầu của Quý khách', 400000, '1700558394-thumb.jpg', 'da4902cb0bc38210839714ebdcf0efc3-details.jpg|b4189d9de0fb2b9cce090bd1a15e3420-details.jpg|b6d67a24906e8a8541291882f81d31ca-details.jpg|7e230522657ecdc50e4249581b861f8e-details.jpg|7ec2442aa04c157590b2fa1a7d093a33-details.jpg|d5fcc35c94879a4afad61cacca56192c-details.jpg', '0354567669', 8, '2023-11-21', '2023-11-21 09:19:54', 'ngõ 385 Hoàng Quốc Viêt', 476, 143, 18, 'https://maps.app.goo.gl/ibJJRHxE8nQ1MBsD6', 'Wifi , Parking , Bed', 1),
(64, '22Housing - 9 ngõ 81 Linh Lang', '22Housing - 9 ngõ 81 Linh Lang là một Khách sạn 3 sao đẹp có địa chỉ ngay tại Số 9 ngõ 81 Linh Lang, Phường Cống Vị, Ba Đình, Hà Nội, đường đi rất thuận tiện và dễ tìm. 22Housing - 9 ngõ 81 Linh Lang có đội ngũ nhân viên chuyên nghiệp, luôn cố gắng phục vụ mọi nhu cầu của khách hàng, vui lòng khách đến, vừa lòng khách đi, phòng ốc của 22Housing - 9 ngõ 81 Linh Lang sạch đẹp, đầy đủ tiện ích trong phòng, có đầy đủ nóng lạnh, internet. Các cặp đôi đặc biệt thích địa điểm 22Housing - 9 ngõ 81 Linh Lang và họ đã cho 4 điểm sau khi nghỉ tại đây. Chỗ nghỉ này cũng được đánh giá là đáng giá tiền nhất ở quanh khu vực, bạn sẽ tiết kiệm được nhiều hơn so với các chỗ nghỉ khác.', 500000, '1700558566-thumb.jpg', '0a93091da5efb0d9d5649e7f6b2ad9d7-details.jpg|263d532e4904460675006ad964948efa-details.jpg|a60937eba57758ed45b6d3e91e8659f3-details.jpg|90e69a6d2ad189b222ac1998abe63aea-details.jpg|4589b8b6a21b964025a84f6499070b83-details.jpg|8d3215ae97598264ad6529613774a038-details.jpg|43d762ca733a839226415450b0dbf9d2-details.jpg', '0354567669', 10, '2023-11-21', '2023-11-21 09:22:46', 'Số 9 ngõ 81 Linh Lang', 296, 135, 18, 'https://maps.app.goo.gl/ibJJRHxE8nQ1MBsD6', 'Wifi , Parking , Bed', 1),
(65, 'Ann House', 'Một homestay được chủ nhà chăm chút từng góc nhỉ. Mình muốn khách đến nhà Ann House phải thật dễ chịu, thoải mái mọi thứ được sắp xếp đầy đủ như đang ở nhà nhưng không gian lại mang nhiều trải nghiệm mới cho khách hàng.', 300000, '1700558928-thumb.jpg', '83eaa6722798a773dd55e8fc7443aa09-details.jpg|eeea8c180c5dff16f68a6b7e2606b430-details.jpg|a9365bd906e11324065c35be476beb0c-details.jpg|c6447300d99fdbf4f3f7966295b8b5be-details.jpg|812649f8ed0e2e1d911298ec67ed9e61-details.jpg|1343777b8ead1cef5a79b78a1a48d805-details.jpg|1e51e0f3b6b60070219ccb91bb619a6b-details.jpg', '0354567669', 6, '2023-11-21', '2023-11-21 09:28:48', '26B ngõ 1 Đình Thôn', 490, 140, 18, 'https://maps.app.goo.gl/6YXaJLRt9J1TX29m7', 'Wifi , Parking , Bed', 2),
